import React,{useState} from "react";

function Register(){
    const handleSubmit= async (e)=>{
        const res = await fetch("http://127.0.0.1/api/register",{
         method:"post",
         headers:{"Content-Type": "application/json"},
         body:JSON.stringify({
          name:e.target.name.value,
          email:e.target.email.value,
          password:e.target.password.value,
          confirm_password:e.target.confirm_password.value,
         }),
        })

        const data = await res.json();
        localStorage.setItem("token",data.token);
        alert("registered");
    }
    return (
        <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Inter your names"/>
        <input name="email" placeholder="Inter your valid email"/>
        <input name="password" placeholder="Inter y password"/>
        <input name="confirm_password" placeholder="inter password again"/>
        <button> Submit</button>
        </form>
        
    )
}
export default Register;
